import React, { Component } from 'react';
import FacilityService from "../services/FacilityService";
import VoteService from "../services/VoteService";

class Votes extends Component {

    constructor(props) {
        super(props);
        this.state = {
            title: '',
            description: '',
            manager: {idManager : 1},
            votes :  [],
            vote : {
                idVote : ''
            },
            numberOfVotesInFavour : 0,
            numberOfVotesAgainst : 0,
            selectOptions : []
        }
    }

    async fetchVotes() {
        console.log("------ Fetch Votes ------")
        const res =  await VoteService.getVotes()
            .then((response) => {
                const options = response.data.map(d => ({
                    "value" : d.idFacilities,
                    "label" : d.name
                }))
                this.setState({facilities: options})
            })
            .catch((error) => {
                console.log(error);
            });
    }

    render() {
        return (
            <div className='row'>
                <div className='col-md-4 '>
                    <div>
                        <h2 style={{fontSize: '30px', color: '#757575', marginBottom: '35px'}}>Votes</h2>
                    </div>
                    <table style={{width: '250px'}}>
                        <tr style={{ backgroundColor: 'white'}}>
                            <td><a href='#'>Alfreds Futterkiste</a></td>
                        </tr>
                        <tr>
                            <td><a href='#'>fsdf</a></td>
                        </tr>
                        <tr>
                            <td><a href='#'>sd</a></td>
                        </tr>
                        <tr>
                            <td><a href='#'>fsd</a></td>
                        </tr>
                        <tr>
                            <td><a href='#'>sdfsd</a></td>
                        </tr>
                        <tr>
                            <td><a href='#'>sdfsdf</a></td>
                        </tr>
                    </table>
                </div>
            </div>
        );
    }
}
export default Votes;
