import React, { Component } from 'react';
import FacilityService from "../services/FacilityService";
import VoteService from "../services/VoteService";


class Votes extends Component {

    constructor(props) {
        super(props);
        this.state = {
            title: '',
            description: '',
            manager: {idManager : 1},
            votes :  [],
            vote : {
                idVote : ''
            },
            numberOfVotesInFavour : 0,
            numberOfVotesAgainst : 0,
            selectOptions : []
        }
    }

    async fetchVotes() {
        console.log("------ Fetch Votes ------")
        const res =  await VoteService.getVotes()
            .then((response) => {
                this.setState({votes: res.data})
            })
            .catch((error) => {
                console.log(error);
            });
    }

    render() {
        return (
            <div className='row'>
                <div className='col-md-4 '>
                    <div>
                        <h2 style={{fontSize: '30px', color: '#757575', marginBottom: '35px'}}>Votes</h2>
                    </div>
                    <table style={{width: '250px'}}>
                        <tr style={{ backgroundColor: 'white'}}>
                            <td><a href='#'>Alfreds Futterkiste</a></td>
                        </tr>
                        <tbody>
                        {this.state.votes.length===0?
                            <tr align="center"><td colSpan="5">No Record Found</td></tr>:
                            this.state.votes.map(
                                (vote,index) =>(
                                    <tr style={{ backgroundColor: 'white'}} key = {vote.idVote}>
                                        <td>{vote.title}</td>
                                    </tr>
                                )
                            )
                        }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}
export default Votes;
